import { ActionTypes } from "../constant/actionTypes";

const initialState = {
    products :[]
}

export const productReducer = (state = initialState , { type, payload}) => {
    switch (type) {
        case ActionTypes.SET_PRODUCTS:
            return {...state,products:payload};
            // break;
    
        default:
            return state;
    }
}
